import { useState } from 'react';
import './UserForm.css';
import Card from "../UI/Card";

const Form = (props)=>{
    const [username,setUsername] = useState('')
    const [age,setAge] = useState('')
    const addUserHandler = (e)=>{
        e.preventDefault();


    }

    const userChangeHandler =(e)=>{
        setUsername(e.target.username.value)

    }

    return(
       
        <Card className="input">
               <form onSubmit={addUserHandler}  onChange={userChangeHandler}>
            <label htmlFor='username' className='form-input'>Username</label>
            <input name="username"id="username" type="text" className='form-input'></input>{username}
            <label htmlFor='age' className='form-input'>Age</label>
            <input id="age" type="number" className='form-input' ></input>
            <button className='form-input button'>Add User</button>
        </form>
        </Card>
        
    )
}

export default Form
